import React, {useState} from 'react';

import Card from '../UI/Card';
import Button from '../UI/Button';
import classes from './AddUser.module.css';

const AddUser = (props) => {
  const [userName, setUserName] = useState('');
  const [userAge, setUserAge] = useState('');
  
    const userNameChangeHandler = (event) => {
        setUserName(event.target.value); 
    };

    const userAgeChangeHandler = (event) => {
        setUserAge(event.target.value);
    };

  const addUserHandler = (event) => {
      event.preventDefault();
      if (userName.trim().length === 0 || userAge.trim().length === 0) {
            alert("Please enter a valid name and age (non-empty values)");
            return;
      }
      
      if (+userAge < 1) { 
          alert("Entered age is invalid");
          return;
      }
      
      props.onAddUser(userName, userAge);
      setUserAge('');   
      setUserName('');
  };

  return (
    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input placeholder='Your name' id="username" type="text" value={userName} onChange={userNameChangeHandler}/>
        <label htmlFor="age">Age (Years)</label>
              <input id="age" type="number" value={userAge} onChange={userAgeChangeHandler}/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
